select
distinct(t.c1) as 编号,t.c700000001 as 工单名称,t.c700000003 as 工单编号,t.c700000006 as 建单时间,t.c700000004 as 建单人,t.c700000011 as 主题,t.c700000015 as 紧急程度,t.c700000014 as 所属专业,t.c700000010 as 工单状态
from {1000} t,(select * from {600} union all select * from {700}) pro 
where
t.C1=pro.c700020001 
{1}  
{2}  
{3} 
{4}
and pro.c700020002=t.c700000001
and pro.c700020004<>'BEGIN'
and (pro.c700020011='已完成' or pro.C700020011='已派发')
